css:
@-moz-document domain("nextdoor.com") {
.sponsored-post-container, .gam-ad-outer-container, .programmatic-promo-container, .promo-container {
    height: 6em;
    margin-bottom: 1em;
    overflow: hidden;
    border-bottom: dashed orange 5px;
}

.sponsored-post-container:hover, .gam-ad-outer-container:hover, .programmatic-promo-container:hover, .promo-container:hover {
    height: auto;
    border-bottom: none;
}
}
