/* i really want this to be global */
/* ... because there is no alternative approach */

/**
 * Design
 **********************************/
body {
    background-color: #111111;
}
.navheader, .navfooter {
    display: none;
}
.article {
    max-width: 960px
}
.article table td {
    padding-right: 10px;
    padding-left: 10px;
}
.article .question td {
    background-color: #222222;
    padding-top: 5px;
    padding-bottom: 5px;
}
.article .answer td {
    padding-top: 20px;
    padding-bottom: 20px;
}
.article .answer td:last-of-type {
    padding-bottom: 40px;
}
.article .title {
    margin-top:30px;
    margin-bottom: 20px;
    padding-bottom:10px;
    border-bottom: 2px solid black;
}
.article .screen {
    background: #000000;
    padding: 10px;
}

/**
 * Typography
 **********************************/
body, .article a, .article table a {
    color: #999999;
    font: 16px/24px"Verdana", "Arial", "sans-serif" !important;
}
.article h3 {
    font-size: 1.4rem;
}
.article p {
    line-height: 1.6rem;
    color: #DDDDDD;
}
.article a {
    color: #AAAAFD !important;
    text-decoration: none;
}
.article a:hover {
    color: #CCCCFF !important;
    text-decoration: underline;
}
.article pre, .article code {
    color: #FFFFFF;
    font-family: Consolas;
    line-height: 1.2rem;
}
.article .question td {
    font-weight: 700;
    font-size: 1.2rem;
    line-height: 1.2rem;
}
.article .title {
    text-align: left !important;
}
