css:
@-moz-document url-prefix("http://infinitenoveltranslations.net/the-spearmaster-and-the-black-cat/"), url-prefix("http://infinitenoveltranslations.net/summoned-slaughterer/"), url-prefix("http://infinitenoveltranslations.net/nidoume-no-jinsei-wo-isekai-de/"), url-prefix("http://infinitenoveltranslations.net/hakai-no-miko/"), url-prefix("http://infinitenoveltranslations.net/hachinan-tte-sore-wa-nai-deshou/"), url-prefix("http://infinitenoveltranslations.net/ascendance-of-a-bookworm/"), url-prefix("http://infinitenoveltranslations.net/teasers/imperial-wars-and-my-stratagems/"), url-prefix("http://infinitenoveltranslations.net/kakei-senki-wo-kakageyo/"), url-prefix("http://infinitenoveltranslations.net/maou-no-utsuwa/") {
/* Code to Style the Text and Scrollbar */
h1, h2, h3, h4, h5, h6, h7, h8, h9 {
    color: #eeeeee !important;
    font-family: Helvetica, Arial, sans-serif !important;
}

hr {
    border-color: #555555 !important;
}

a {
    color: #c69c6d !important;
}

p {
    color: #eeeeee;
    font-family: Helvetica, Arial, sans-serif !important;
    font-size: 22px;
    line-height: 32px;
    margin: -10px -10px 20px -10px;
    padding: 10px;
    border-radius: 5px;
    text-indent: 0;
}

html {
    scrollbar-color: #555555 #333333;
}

.html::-webkit-scrollbar-track-piece {
    background-color: #333333;
}

.html::-webkit-scrollbar-thumb:vertical {
    height: 30px;
    background-color: #555555;
}

div {
    background-color: #333333 !important;
    border-bottom-color: #333333
}

/* Code to center Novel text */
 div[id="forbottom"] {
    max-width: 700px !important;
}

div[id="content"] {
    width: 700px !important;
    margin: 0 auto !important;
}

/* Code to remove content from webpage except for the Novels text and navigation. */
 div[id="wp_access_helper_container"] {
    display: none;
}

div[id="topbar"] {
    display: none !important;
}

div[id="header-full"] {
    display: none;
}

div[id="toTop"] {
    display: none !important;
}

footer[id="footer"] {
    display: none;
}

li[id="eu_cookie_law_widget-2"] {
    display: none !important;
}

div[id="disqus_thread"] {
    display: none;
}

div[id="secondary"] {
    display: none;
}
}
