css:
@-moz-document domain("allkeyshop.com"), domain("cheapdigitaldownload.com"), domain("keyforsteam.de"), domain("goclecd.fr"), domain("clavecd.es"), domain("cdkeyit.it"), domain("cdkeypt.pt"), domain("cdkeynl.nl") {
body,
body.custom-background {
    background-color: #171a21;
}

body.custom-background {
    background-image: none;
}

.bg-link-left,
.bg-link-right {
    display: none;
}

.content > .row > .col-4,
.content-box.gallery, 
.game-desc .content-box-title {
    display: none !important;
}

.content > .row > .col-xl-8 {
    margin-left: auto;
    margin-right: auto;
}

.tab-content [itemprop=description] {
    height: 170px !important;
}
}
