css:
body {
    background: #000 !important;
}

.wrap {
    background: linear-gradient(to top, #000, #005557);
    border: 2px solid #00F2F8;
    box-shadow: 0 0 10px #8BF5F8;
}

.lounge-nav {
    background: linear-gradient(to right, #00F2F8, #005557)
}

.room-name {
    color: #f3f3f3
}

.creator {
    color: #00F2F8;
}

.lounge-top a {
    color: white;
    transition: .2s;
}

.lounge-top a:hover {
    color: #00F2F8 !important;
}

.lounge-counter {
    color: #000;
}

.btn-default {
    background: transparent;
    color: #000;
    transition: .3s;
    border: 2px solid #000;
    border-radius: 20px;
    font-weight: bold;
}

.btn-default:hover {
    border-color: #f3f3f3;
}

.progress-bar-label-wrap {
    color: #00F2F8;
}

.rooms-filter-wrap {
    background: #005557 !important;
    border: none;
    border-radius: 2px;
}

.rooms-filter-wrap label {
    color: white !important;
}

#rooms-filter {
    background: #004142;
    border: none;
    border-radius: 2px;
    color: white;
}

.highlight {
    background: #004142 !important;
    border: none;
    border-radius: 2px;
}

.highlight li {
    color: white;
}

.highlight li a {
    color: white !important;
}

.tripcode .members {
    color: #00F2F8;
}

.avatar-wrap {
    color: #00F2F8 !important;
}

/* i really want this to be global */
