css:
@-moz-document domain("hub.github.com") {

  header,
  section p em {
    background-color: #4E4845;
  }

  body {
    background-color: #33302F;
  }

  h2,
  p {
    color: #F6F6F6;
  }

  a:link {
    color: #E98B3D;
  }

  h1 a {
    -webkit-text-stroke: #33302F 1px;
    text-shadow: #413E3C -1px -1px 0, #413E3C -2px -2px 0, #413E3C -3px -3px 0, #413E3C -4px -4px 0, rgba(65, 131, 196, .3) -5px -5px 0;
  }

}
