css:
@-moz-document url-prefix("http://bugs.server.com") {
/*Dark Stuff*/
/*dark-dark*/
#bugzilla-body, .bz_row_even, #attachment_table th, .bz_alias_short_desc_container, .selected, .tabbody, .selected_section, .sortlist_separator {
    background-color: #292929 !important;
    color: #d5d5d5;
}
/*light-dark*/
body, html, .links, #footer, input, .bz_row_odd, .field_label, .bz_time_tracking_table th, #attachment_table td, textarea, select, .bz_comment, .clickable_area, #menu td, .yui-ac-content, .yui-dt-liner {
    background-color: #2f2f2f !important;
    color: #d5d5d5;
}


/*text*/
/*text general*/
.component_description, .text_input, .yui-dt-label {
    color: #d5d5d5 !important;
    background-image: none !important;
}

/*links*/
a, a:hover {
    color: #929bb1 !important;
}

}
