css:
 @-moz-document domain("roblox.com")
{
/*[[ad-key]]*/  :root
  
#navigation-container a.btn-logo,

#navigation-container a.btn-logo:visited,

#navigation-container a.btn-logo:hover
  {
    background-image: url(http://imgur.com) !important;
  }

  .site-header .HeaderDivider
  {
    border-left: 1px solid #878988 !important;
  }

  .site-header
  {
    background-image: url(http://i.imgur.com/TCHoQBj.png) !important;
  }

  body
  {
    background-color: #E3E3E3;
  }



/*[[animation]]*/

  html
  {
    font-family: 'Demonized Regular', Cursive ,sans-serif !important;
  }

  *
  {
    font-family: inherit !important;
  }

  /* Reset text color */

  h1 a,

  h1 a:visited,

  h1 a:active,

  h1 a:link,

  .StandardBoxHeader,

  .StandardBoxHeaderGray,

  .stat-label,

  .stat,

  .form-label,

  .checkbox-label,

  .totals-label,

  .item-date,

  .totals-label span,

  .footnote,

  .SortFilterLabel,

  #ItemContainer.PlaceItemContainer.PlaceItemContainer .item-header,

  #Footer.LanguageRedesign .Legalese,

  #left-column .StandardBox,

  #mid-column .StandardBox,

  #right-column .StandardBox,

  .LoginFormLabel,

  .LoginFormFieldSet > span:first-child
  {
    color: inherit !important;
    font-family: inherit !important;
  }
}
