css:
@-moz-document domain(skribbl.io) {
.content , .overlay , .wordContainer{
    position:absolute;
    top:0px;
    left:0px;
    right:0px;
    height:50px;
}
#currentWord {
    display:inline-block;
    min-width:500px;
    flex-shrink:0;
    flex-grow:0;
    position:relative;
    top:54px;
    left:20px;
    border-radius:15px;
    border:1px solid black;
    background:white;
    font-size:22px;
}
#canvasGame {
    margin-top:50px !important;
}
}
