css:
@-moz-document domain(vidcloud9.com) {
/*general page width*/
#wrapper{
    max-width:100%;
}
.main-content{
    width:72%;
}

/*commons*/
#main_bg{
    background:#333;
}
#navigation-wrapper{
    background:#33383c;
}
ul.items li .name, .video-info-left h1{         /*titles on the homepage and movie page*/
    color:#FFFd;
}
#header_search_autocomplete ul{
    font-size:32px!important;
    background:#444;
    border-radius:13px;
}
#header_search_autocomplete li a{
    color:#eee!important;
    font-size:17px!important;
}
#navigation-wrapper ul#menu-header-menu li a{
    border-radius:9px;
}
.img{                                           /*rounded borders for posters*/
    border-radius:7px;
}
#footer{
    margin-top:0px;
}

/*video page*/
.video-details{
    color:#fffd;
    font-size:18px;
    
}
.video-options .option{                         /*comment, share and lights off buttons border*/
    border-radius:9px;
    border-color:#fff8;
}
}
