css:
@-moz-document domain("mail.google.com") {
/* left sidebar */
.nH.oy8Mbf.nn.aeN.bhZ, .nH.oy8Mbf.nn.aeN {
    display: none;
}

/* message list */
.bhZ+.bkK {
    margin-left: 0px !important;
}

/** top bar **/
.gb_lc, .gb_td {
    height: unset;
}

/* left gmail icon */
.gb_lc.gb_qc.gb_sc {
    display: none;
}

/* search mail button */
[aria-label="Search Mail"] {
    display: none;
}

/* advanced search option button */
[aria-label="Advanced search options"] {
    display: none;
}

/* right side user and support buttons */
.gb_mc.gb_Ja.gb_lc.gb_Jd {
    display: none;
}

/* tooltip help button */
.gb_fe.gb_yd {
    display: none;
}

/* search bar */
.gb_Ie, .gb_2e {
    padding: 0px !important;
    height: 20px !important;
}

/** category tab **/
/* tab subtext */
.aKs {
    display: none;
}

/* tab icon */
.aKp {
    display: none;
}
}
