css:
@-moz-document domain("summit.news") {
a.mvp-nav-logo-small {
    display: none;
}

@media screen and (max-width: 1004px)  {
    a.mvp-nav-logo-small {
    display: block;
}
}

body {
    margin-top: 20px;
}

.mvp-nav-top-mid.left.relative {
    display: none;
}

span.mvp-nav-search-but {
    cursor: pointer;
    float: right;
    font-size: 20px;
    line-height: 1;
    padding: 35px 0;
}

ul#menu-main-menu-1 {
    display: flex;
    margin-top: 20px;
    margin-left: 160px;
    position: relative;
}


.mvp-nav-bot-left.left.relative {
    margin-top: 32px;
}

ul#menu-main-menu-1:after {
    content: "";
    position: absolute;
    background: url("https://s3.us-east-2.amazonaws.com/x929147wdklad/summitnews.svg");
    background-repeat: no-repeat;
    background-position: 20px;
    height: 50px;
    width: 150px;
    top: -14px;
    left: -160px;
}

div#mvp-main-nav-bot {
    margin: 0 0 50px 0;
}
}
