css:
@-moz-document domain("memrise.com") {
body #content, body > .explore {background-color:#D3C58F}
select {background:#B1D8E5; color:#000}

/* Homepage */
.mobile-app-container, .explore-container::before, .explore-container::after, .explore ~ .love, .love-container::before, .love-container::after, .explore ~ .waiting-container {background:#D3C58F}

/* Persistant "moving to decks" notification */
.sel-dashboard .card-decks-moving {display:none !important}
.courses-filter-container.with-decks-moving {margin-top:-83px; margin-bottom:0}

/* Header */
.header-nav .header-nav-item::after, .rebrand-sign-up {background:#D3C58F}

/* Continue learning button */
.single-continue-button.single-continue-button {background:#B1D8E5}
.single-continue-button-text {font-size:14px; text-align:center}
.single-continue-button-icon, .single-continue-button-arrow {display:none}
.single-continue-button-label {padding:0 !important; margin:0 !important}
.mode-selector-modal-content .single-continue-button-text {max-width:inherit}

/* Progress bars */
.course-card-container .course-progress-box .card-top .card-main-container .course-progress .progress-bar-container .progress-bar, .header-profile-inner .profile-row.profile-progress .profile-progress-bar {background:#ffda3e}

/* Community header */
.d-header {background:#2B3648}
}
