css:
@-moz-document regexp(".*clngdbkpkpeebahjckkjfobafhncgmne.*"), regexp(".*748b8bf2-f2e4-4940-88c5-d43396526585.*") {
:root
{
    --text: /*[[text-key]]*/;
    --container: /*[[color-key]]*/;
    --button: /*[[btn-color-key]]*/;
    --button-light: /*[[h-btn-color-key]]*/;
}
a
{
    color: var(--text) !important;
}
.svg-icon
{
    fill: var(--text) !important;
}
#stylus-edit, select, #stylus-manage, .wide, #help-popup, #dialog *, #stylus-popup, .search-result, .search-result-meta, #hotkey-info div, .dropdown-content a, #message-box div, #confirm div
{
    background: var(--container) !important;
    color: var(--text) !important;
    border: none !important;
}
button, select, input
{
    background: var(--button) !important;
    color: var(--text) !important;
    border: none !important;
}
button:hover, select:hover, .dropdown-content a:hover
{
    background: var(--button-light) !important;
}
.checkmate
{
    visibility: hidden !important;
}
}
