/* Partly from https://github.com/thebaer/MMRA/blob/master/medium.css , MIT License */

/**
 * medium.css
 * Contains default styles to inject into Medium publications.
 */
.overlay,
.postMeterBar,
.openInAppButton,
.js-stickyFooter,
.js-upgradeMembershipAction,
.butterBar--privacy {
display: none;
}

/* Makes the page scrollable after hiding "Pardon the interruption" popup */
html.u-overflowHidden {
overflow-y: scroll !important;
}

/* Makes the metabar scrollable so that it won't take too much height */
.metabar.u-fixed {
position: absolute !important;
}

}
