css:
@-moz-document domain("thatseed.org") {
body, input, textarea, keygen, select, button {
    font-family: inherit;
    color: #ec95cc;
    background-color: #000000e0;
}

.thatseed-panel-node-title {
    position: relative;
    font-size: 2em;
    color: #ffd5ea;
}
.thatseed-menu-frame {
    background-color: #222121;
box-shadow: 0 0 3em 0.9em #624f59;
}

.thatseed-panel-node{
     transition: 0.3s;
}
.thatseed-panel-node:hover {
    background-color: #1f1f1f;
    box-shadow: 0px 2px 4px 2px black;
}

.thatseed-maskLayer{
    background-color:#000000cf;
}
.thatseed-menu-buttons div:hover {
    background: rgb(29, 25, 27);
    color:#afa7a7;
}

.hero{
    box-shadow: inset 0 0 #000000;
    background-color: #922a55;
}
.site-info {
    background-color: #111010;
}
.site-header {
    background-color: #1f1f1f;
}
.site-header a{
    background-color: #1f1f1f;
}
.site-header a:hover{
    background-color: #60485a;
}
.site-info-wrapper {
    background: #000;
}



#thatseed-panel-nodepad{
    overflow-y:hidden;
}
.thatseed-panel-qrcode-title{
    color:#9f9999;
    background-color:#0000005c;
    box-shadow:0 2px #832995;
}
.thatseed-panel-qrcode-layer{
   box-shadow:0px 2px 10px 4px #ff4de673
}

.thatseed-panel-category-button:hover{
	color:white;
	text-shadow: 0 0 10px  #fb00ff;
	border-bottom: 0.15em #ff24ee solid;
}

.site-info-wrapper {
    background: #111010;
}
}
