css:
@-moz-document domain("bitseven.com") {
#wrapper {
    min-width: initial;
}
#header .menu-main ul li a {
    font-size: 1.34em;
}
.the-header {
    height: 100%;
}
.content-page-small {
    max-width: none;
    margin-right: 260px;
}

.container {
    max-width: initial;
}
.trading-chart > div {
    width: 100% !important;
}
.trading-recent, .table-trading tr {
    display: none;
}
.table-striped2 tr:first-child, .table-striped2 tr:nth-child(2), .table-striped2 tr:last-child, .table-striped tr:first-child {
    display: table-row;
}
}
