css:
@-moz-document url-prefix("https://osu.ppy.sh/users/") {
.osu-page-header-v3--users .osu-page-header-v3__icon {
	box-shadow: inset 0 0 0 2px /*[[lines]]*/;
}

.osu-page-header-v3--users .osu-page-header-v3__title-highlight {
    color: /*[[lines]]*/;
}

.game-mode-link {
 	color: /*[[lines]]*/;   
}

.page-mode-v2--users:after {
	background-color: /*[[lines]]*/;   
}

.page-mode-v2--users .page-mode-v2__link:before {
 	background-color: /*[[lines]]*/;   
}

.page-mode-v2--users .page-mode-v2__link {
 	color: /*[[lines]]*/;   
}

.page-extra-tabs--profile-page {
 	border-bottom-color: /*[[lines]]*/;   
}

.page-mode-link--profile-page {
 	color: /*[[lines]]*/;   
}

.page-mode-link--profile-page .page-mode-link__stripe {
 	background-color: /*[[lines]]*/;   
}

.page-extra__title {
 	border-bottom: 2px solid /*[[lines]]*/;
}

.page-extra__title--small {
 	border-bottom: none;   
}

/* experimental */
::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}
::-webkit-scrollbar-button {
  width: 10px;
  height: 10px;
}
::-webkit-scrollbar-thumb {
  background: #4a4a4a;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-thumb:hover {
  background: /*[[lines]]*/;
}
::-webkit-scrollbar-thumb:active {
  background: /*[[lines]]*/;
}
::-webkit-scrollbar-track {
  background: #222222;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-track:hover {
  background: #222222;
}
::-webkit-scrollbar-track:active {
  background: #000000;
}
::-webkit-scrollbar-corner {
  background: transparent;
}
}
