css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fandom.com") {
/* main */
div.main-container {margin-left:0px;width:100%!important;padding-left:66px;}
div.resizable-container {display:block!important;max-width:100%!important;min-width:100%!important;margin-left:0px!important;margin-right:0px!important;box-sizing:border-box!important;}
div.page {width:85%!important;display:block!important;float:right;box-sizing:border-box;}
div.page.has-right-rail {display:grid!important;grid-template-areas:
    'main'
    'footer';
}
main.page__main {display:block;width:100%!important;grid-area:main;box-sizing:border-box!important;font-size:90%;background-image: linear-gradient(to right, white , #ddeeff);border-bottom-style:solid;border-bottom-width:1px;border-bottom-color:#666666;border-left-style:solid;border-left-width:1px;border-left-color:#888888;border-top-style:solid;border-top-width:1px;border-top-color:#aaaaaa;}
div#mw-content-text p, table, div {line-height:110%;}
/* footer */
aside.page__right-rail {display:block!important;position:static!important;grid-area:footer;width:100%!important;background-color:white;border-bottom-width:1px;border-bottom-style:solid;border-bottom-color:#666666;column-count:3;}
aside.page__right-rail div.right-rail-wrapper {background-color:white;}
div#rail-boxad-wrapper {display:none;}
div.sticky-modules-wrapper {background-color:white;}
section#page-tools-module {border-bottom-width:0px;}
div.sticky-modules-wrapper section {background-color:white;}
div#mixed-content-footer {max-height:400px;overflow-y:scroll;overflow-x:auto;}
button.content-size-toggle {display:none;}
button.right-rail-toggle {display:none;}
/* menu */
div.community-header-wrapper {display:block;width:15%!important;box-sizing:border-box!important;position:static;z-index:4;float:left;}
header.fandom-community-header {display:grid;box-sizing:border-box!important;grid-template-areas:
    'image'
    'head'
    'content';
}
a.fandom-community-header__image {grid-area:image;margin-right:0px;width:100%!important;overflow:hidden;}
a.fandom-community-header__image img {margin-left:auto;margin-right:auto;width:100%;}
div.fandom-community-header__top-container {margin-right:auto;margin-left:auto;width:100%!important;grid-area:head;display:grid;grid-template-areas:
    'title counter'
    'tools tools';
}
div.fandom-community-header__community-name-wrapper {grid-area:title;overflow:hidden;padding-left:10px;}
div.wiki-tools {grid-area:tools;margin-left:auto;background-color:rgba(128,128,128,0.75);margin-right:15px;margin-top:10px;}
div.page-counter {grid-area:counter;padding-right:10px;margin-top:5px;}
nav.fandom-community-header__local-navigation {box-sizing:border-box!important;padding-left:10px;padding-right:10px;margin-top:15px;grid-area:content;display:block!important;width:14vw!important;}
nav.fandom-community-header__local-navigation ul.extra-large-navigation {display:block!important;box-sizing:border-box!important;}
nav.fandom-community-header__local-navigation ul.extra-large-navigation div.wds-dropdown {border-style:solid;border-color:rgba(255,255,255,0.75);border-width:1px;background-color:rgba(64,64,64,0.75);margin-bottom:10px;box-shadow: 5px 5px 3px rgba(0,0,0,0.25);border-radius:3px;box-sizing:border-box!important;width:100%;}
nav.fandom-community-header__local-navigation ul.extra-large-navigation div.wds-dropdown__content {display:block;position:relative;background-color:transparent!important;border-width:0px;border-style:none;box-sizing:border-box!important;padding-top:0px;}
nav.fandom-community-header__local-navigation ul.extra-large-navigation div.wds-dropdown__content > ul.wds-list > li > a {color:white;padding-top:5px;padding-bottom:5px;line-height:100%;font-size:90%;}
div.wds-dropdown__content ul {box-sizing:border-box!important;}
div.wds-dropdown__content ul li {box-sizing:border-box!important;}
div.wds-dropdown__content ul li a {box-sizing:border-box!important;}
div.wds-tabs__tab-label {padding-left:15px;}
div.wiki-tools div.wds-dropdown__content {z-index:401!important;}
div.fandom-sticky-header {display:none!important;}
/* minor alterations */
nav#articleCategories {border-style:solid;border-color:#444444;border-width:1px;border-radius:3px;padding-top:10px;padding-left:10px;background-color:#eeeeee}
div.page-header {border-bottom-style:solid;border-bottom-width:1px;border-bottom-color:#666666;}
div#toc ul {background-color:#eeeeee;}
div#toc div.toctitle {background-color:#ddeeff;}
}
