css:
@-moz-document domain(hpacademy.com) {
body {
    color: #AAA;
    background-color: #000;
}

.main-header__top,
.header__top__nav__items,
div.constrainer,
nav.main-header_top_nav {
    color: #EEE;
    background-color: #111
}

div.main-content,
div.row,
div.container,
p,
div.admonition,
p.first,
p.last,
div.content,
div.user-details, 
section.typography,
header.site-header {
    color: #EEE;
    background-color: #111;
}

.modules .module, .dashboard {
    color: #EEE;
    background-color: #111;
}
.video-list, div.dashboard-notifications {
    color: #EEE;
    background-color: #333;
}

article,
tt,
pre,
span.pre,
tt.literal {
    color: #CCC;
    background-color: #333;
}

div.bodywrapper,
div.col-sm-8,
div.col-sm-4,
div.col-xs-6,
div.col-sm-5,
div.col-md-3,
div.cta-text{
    color: #AAA;
    background-color: #111;
}

div.bodywrapper dt,
div.bodywrapper dd {
    color: #AAA;
    background-color: #111;
}

div.bodywrapper pre {
    color: #AAA;
    background-color: #111;
}

div.bodywrapper li {
    color: #AAA;
    background-color: #111;
}
}
