css:
@-moz-document regexp('https?://(.+\.)?memrise\.com/course/[^/]+/[^/]+/[^/]+/garden/learn/.*') {

#central-area {
    width: auto;
    text-align: center;
}

.garden-box.multiple_choice ol.choices.grid li, .garden-box.video-pre-presentation ol.choices.grid li {
    display: inline-block;
    float: none;
}
.garden-box.multiple_choice ol.choices.grid li:nth-child(2n), .garden-box.video-pre-presentation ol.choices.grid li:nth-child(2n) {
    float: none;
}

}
