css:
@-moz-document url("https://sonic-pi.net/tutorial.html") {
body {
    font-family: sans-serif;
}

#doc-nav {
    color: #dddddd;
}

#doc-footer {
    border-width: 1px;
    border-top-style: solid;
    border-color: #333333;
}

h1 {
    border-radius: 5px;
	box-shadow: 0 .125rem .25rem rgba(0,0,0,.125) !important;
}

h2 {
    border-radius: 3px;
	box-shadow: 0 .125rem .25rem rgba(0,0,0,.075) !important;
}

pre {
    border-radius: 5px;
}
}
