css:
@-moz-document domain("tweakers.net") {
span.subtitle {
    color: white
}

a {
    color: gray
}

#layout {
    background-color: #323639;
}

#searchbar {
    background-color: #323639;
}

#header h1 .subtitle {
    color: gray;
}

.pageTabsContainer {
    background-color: #323639;
}

#entity {
    background-color: #323639;
}

#contentArea {
    background-color: #393e42;
}

#categoryBrowser {
    background-color: #323639;
}

#categoryBrowser .sublist{
    background-color: #323639;
}

#categoryBrowser li {
    background-color: #323639;
}

#categoryBrowser li > a {
    color: gray
}

#categoryBrowser li:hover {
    background-color: rgb(38, 41, 43);
}

#categoryBrowser .images li:hover {
    box-shadow: inset 0 0 25px lightgray, inset 0 0 0 1px rgba(0, 0, 0, 0.1);
}

#categoryBrowser .images li {
    background-color: #323639
}

.article {
    color: white
}

.article a {
    color: lightblue
}
  
.article a:hover {
    color: gray
}

a.ellipsis {
    color: white;
}

a.ellipsis:visited {
    color: lightgray;
}

p.ellipsis > a {
    color: white;
}

p.ellipsis > a:visited {
    color: lightgray;
}

.pwPortalListing p.price a {
    color: gray;
}

.pwPortalListing table.listing p {
    color: gray
}

#categoryBrowser .large li a {
    color: gray;
}

.fancyButton {
    background-color: #00a2ff;
    background-image: none;
    border-radius: 4px;
}

.fancyButton:hover {
    background-color: #0091ff;
    background-image: none;
    border-radius: 4px;
}

.fancyButton.white {
    background-color: gray;
    border: none;
    border-radius: 4px;
}

.fancyButton.white:hover {
    background-color: #939393;
    border: none;
    border-radius: 4px;
}

.fancyButton.white span.checkbox {
    color: white;
}
}
