css:
@-moz-document domain("semlar.com") {
/* BASIC STUFF */

body {
    background-color: rgb(20, 20, 20);
    background-image: url('https://i.imgur.com/GKSSg7S.png'), url('https://i.imgur.com/CrMOMLX.png');
    color: #fff
}

element, .navbar {
    background-color: #222 !important;
    border-bottom: 1px solid #ddd;
}

a {
    color: #6fbdff;
    text-decoration: none;
}

.panel {
    margin-bottom: 20px;
    background-color: #2a2a2a;
    border: 1px solid rgb(83, 83, 83);
    border-radius: 4px;
}

/* PANEL FIXES */

.col-md-6 {
    background-color: #fff0;
    border-radius: 4px;
}

.rivenhunter-container {
    background-color: rgba(20, 20, 20, 0);
}

#weapon-stats,
.table .table {
    background-color: #fff0;
}

.statlist tr:hover td, 
#clipTable td:hover {
    background-color: #2a4a77cf;
    cursor: pointer;
}

#dispositions li:hover {
    background-color: #2a4a77cf;
}

#riven-table tr:hover td {
    background-color: #2a4a77cf;
}

.table tr:hover td {
    background-color: #2a4a77cf;
}
}
