css:
@-moz-document domain("elinux.org") {
#mw-head {
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
    background-color: black;
}

#mw-panel {
    font-size: inherit;
    position: absolute;
    top: 0;
    width: 10em;
    left: 0;
    background-color: black;
}

.mw-body {
    margin-left: 10em;
    border: 1px solid #d61f28;
    border-right-width: 0;
    margin-top: -1px;
    background-color: black;
    color: white;
}

.mw-body .firstHeading {
    overflow: visible;
    color: white;
}


}
