css:
@-moz-document domain("www.youtube.com") {
::-webkit-scrollbar {
    display: none;
}

app-drawer[opened] {
    visibility: hidden;
}

ytd-app[guide-persistent-and-visible] ytd-page-manager.ytd-app,
app-drawer[persistent],
ytd-app[mini-guide-visible_] ytd-page-manager.ytd-app {
    margin-left: 0;
    margin:0;
}

ytd-browse[guide-persistent-and-visible] ytd-two-column-browse-results-renderer.ytd-browse,
ytd-browse[guide-persistent-and-visible] ytd-video-masthead-ad-v3-renderer.ytd-browse,
ytd-two-column-browse-results-renderer.ytd-browse,
ytd-video-masthead-ad-v3-renderer.ytd-browse {
    width: 95% !important;
}

style-scope ytd-item-section-renderer,
grid-subheader style-scope ytd-shelf-renderer,
style-scope ytd-expanded-shelf-contents-renderer,
yt-horizontal-list-renderer,
ytd-grid-renderer,
ytd-shelf-renderer, 
ytd-video-renderer,
ytd-channel-video-player-renderer,
.ytd-grid-renderer{
    display: flex;
    justify-content: center;
}
}
