css:
@-moz-document domain(store.playstation.com) {

[dir] #shared-nav-container,
.shared-nav-list,
[dir] #jetstream-tertiary-nav .tertiary-container-inner,
[dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link {
    background-color: #000;
    color: #fff;
}

#shared-nav-container.shared-nav-container--minimized .shared-nav__primary-button, #shared-nav-container.shared-nav-container--minimized #sb .sb-my-playstation-link,
.tertiary-logo a .ps-store-text,
.shared-nav-icon-text,
.link-list-item a span,
a.sb-my-playstation-link {
    color: #fff;
}

[dir] #shared-nav .shared-nav__primary-item.active .shared-nav-mobile-icon,
[dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link:hover {
    background-color: #333;
}

[dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link {
    background-color: #000 !important;
}

[dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link {
    border-bottom-width: 0;
}

#shared-nav .shared-nav-button:hover,
[dir] #shared-nav .shared-nav__primary-item.active .shared-nav-mobile-icon {
    color: #fff !important;
}

.tertiary-logo a .ps-store-text,
.shared-nav-search-container svg {
    display: none;
}

.tertiary-logo .ps-store-bag::after {
    display: inline-block;
    padding: 10px 0 0 46px;
    content: 'PlayStation Store';
    white-space: nowrap;
}

.shared-nav-search-container button::after {
    content: '\1F50D';
    color: #fff;
}

.tertiary-secondary-items div a .icon-cart::after,
.tertiary-secondary-items div a .icon-wishlist::after {
    display: inline-block;
    margin: -5px 0 0 -4px;
    content: '\2764';
    color: #fff;
    font-size: 1.4em;
}

.tertiary-secondary-items div a .icon-cart::after {
    content: '\1F6D2';
}

}
