css:
@-moz-document domain('mbradtke.de') {
  body {
    font-family: sans-serif;
    color: #aaa;
    background: #111;
  }

  hr {
    display: none;
  }

  table, td, th, tr {
    border: 0px;
  }

  table table {
    border-collapse: collapse;
    width: 80%;
  }

  table table td {
    border-bottom: 1px solid #aaa;
  }

  table table tr:hover {
    background-color: #222;
  }

  [color="#CC0000"] {
    color: #e16467;
  }
  [color="#FF0000"] {
    color: #e16467;
  }
  [color="#3366FF"] {
    color: #1ebad7;
  }
  [color="#00CC00"] {
    color: #f6cd61;
  }
  a:link {
    color: #aaa;
  }
  a:visited {
    color: #aaa;
  }
  a:hover {
    color: #aaa;
  }
  a:active {
    color: #aaa;
  }
  a font[color="#0000FF"] {
    color: #aaa;
  }
  font[color="#000000"] {
    color: #aaa;
  }
}
