@-moz-document url-prefix("chrome-extension://clngdbkpkpeebahjckkjfobafhncgmne") {
  /* ==UserStyle==
  @name         Stylus Dark Mode
  @description  Simple dark mode for Stylus. Update v1.1.0 includes styling for the user styles management page.
  @namespace    ocor61.blogspot
  @version      1.1.0
  @author       OmeCor <ocor61@gmail.com> (ocor61.blogspot.com)
  @homepageURL  https://ocor61.blogspot.com/
  @license      unlicense
  @preprocessor stylus
  ==/UserStyle== */

  #stylus-edit,
  #stylus-manage {
    background: #242424!important;
    color: lightgrey;
  }
  .style-name-link {
    color: lightgrey;
  }
  input[type="checkbox"]:not(.slider), select {
    border-color: hsl(0, 0%, 32%);
    background-color: hsl(0, 0%, 82%);
  }
  #manage-text a {
    color: lightgrey;
  }
  #to-mozilla-help, #keyMap-help,
  #search-help, #sorter-help, #update-history, #usercss-wiki {
    filter: brightness(175%);
  }
}
