/* ==UserStyle==
@name           Spele.nl dark mode
@namespace      Spele.nl
@version        0.1.0
@description    Dark mode for spele.nl
@author         Joost Sijm
==/UserStyle== */
@-moz-document domain('spele.nl')
{
	body,
    .header,
    .frontpage .footer .colophon,
    .categorypage .main,
    .columns .column,
    #vooxe,
    .comments-form.card .reaction-form-container .reaction-field
    {
        background-color: #222831;
    }
    
    .box,
    .header .header-bottom,
    .search-form .search-field,
    #profile,
    #aside,
    .categorypage .main .category-bar,
    .game,
    .gamepage .control-container,
    .gamepage .main-section,
    .gamepage .main-top .main-top-content .next-game-container .next-game .btn,
    .comments.card .comment,
    .comments-form.card
    {
        background-color: #393e46;
        border-color: #222831;
    }
    
    body,
    #profile,
    #aside a,
    .game a,
    .comments.card .comment,
    .comments-form.card,
    #main-section
    {
        color: #f9f9f9;
    }
    
    .banner
    {
        display: none !important;
    }
}
