css:
@-moz-document domain("open.spotify.com") {
/* Transparent Navbar */
:root {
    --navbar-color: transparent;
}


/* Styles */
.Root__nav-bar {
    grid-area: nav-bar;
     background-color: var(--navbar-color);
    overflow-y: auto;
    width: 230px;
    will-change: transform;
}
}
