css:
@-moz-document domain("gpokr.com"), domain("kdice.com") {
#hd {
    display: none;
}

#outside > div:last-child {
    display: none;
}

.iogc-GamePanel .gwt-InlineLabel {
    display: none;
}

.iogc-GamePanel .iogc-PlayerStatsPanel:last-child .iogc-PlayerPanel-stat {
    font-size: 14px;
    font-weight: bold;
}

.iogc-GamePanel .iogc-PlayerStatsPanel {
    display: inline;
}

.iogc-GamePanel .iogc-PlayerStatsPanel:nth-child(3) > span:first-child {
    float: left;
    margin-left: 2px;
}

.iogc-GamePanel .iogc-PlayerStatsPanel:nth-child(4) > span:first-child {
    float: right;
    margin-right: 4px;
}

.iogc-GamePanel:after {
    content: "";
    display: table;
    clear: both;
}
}
