css:
@-moz-document domain("twitch.tv") {
* { text-shadow: -0.2px -0.1px 0.1px rgba(255, 255, 255, .75), -0.1px -0.2px 0.1px rgba(255, 255, 255, .75), 0.75px 0.5px 1px rgba(0, 0, 0, .25), 0.5px 0.75px 1px rgba(0, 0, 0, .5), 0px 0px 4px rgba(38, 0, 53, .75), 0px 0px 8px rgba(58, 0, 127, .75);
}

.text-fragment {
    font-size: 12pt !important;
    color: rgba(0, 255, 12, .5);
    text-shadow: -0.25px -0.15px 0.3px rgba(213, 255, 231, .3), -0.15px -0.25px 0.3px rgba(255, 255, 255, .3), 0.75px 0.5px 1px rgba(0, 0, 0, .25), 0.5px 0.75px 1px rgba(0, 0, 0, .5), 0px 0px 4px rgba(38, 0, 53, .5), 0px 0px 8px rgba(58, 0, 127, .5);
    line-height: 100% !important;
}

.tw-font-size-5 {
    font-size: 8pt !important;
    color: rgba(0, 255, 179, .48);
    line-height: 88% !important;
}

.channel-header__banner-toggle.channel-header__user.channel-header__user--selected.tw-align-items-center.tw-flex.tw-flex-nowrap.tw-flex-shrink-0.tw-pd-r-2.tw-pd-y-05 {
    font-size: 8pt !important;
    line-height: 88% !important;
}
}
