css:
@-moz-document url-prefix("https://userstyles.org") {
#button_middle { order: 1 }
#style-settings { order: 2 }
#css_text_area_div { order: 3 }

#headline,
#style_info > a,
#information_div,
.adContainer,
#applies_link,
.text_style_page,
#notes_textarea,
#as_userscript,
#discussions-area { 
	order: 4
}

#button_middle {
	margin-top: -50px !important;
	margin-left: 0
}
.css_button, .advanced_button {
	margin-top: 10px;
	margin-bottom: 10px
}
.advanced_button {
	margin-left: 10px
}
.advancedsettings_shown {
	margin-bottom: 10px
}
#css_text_area_div, #style_info > div:nth-child(6) {
	margin-top: 0px
}
#actions_div {
	display: block;
}
#buttons {
	float: right;
}
@media(max-width: 880px) 
{
	#button_middle {
		margin-top: 0 !important;
	}
}
#button_middle
{
	width: 25% !important;
}
}
