css:
@-moz-document url-prefix("https://www.w3.org/TR/selectors-3/") {
body, .toc-sidebar {
    max-width:auto !important;
}

.selectorsReview {
    font-size:11px;
    font-family:Consolas;
    width: 1200px;
    border:1px solid #e6e6e6;
    border-spacing:0px;
}

.selectorsReview thead {
    background-color:#535353;
    font-size:1.2em;
    color:white;
    line-height: 3em;
}

.selectorsReview th, .selectorsReview td {
    border:0px solid black;
    padding: 1px 1em;
}

.selectorsReview tbody td {
    border-top:1px solid hsl(0, 0%, 97%);
    vertical-align: top;
}

.selectorsReview tbody tr:hover {
    background-color:#e8e9ea;
}
}
