css:
@-moz-document domain("reddit.com") {
div._1OVBBWLtHoSPfGCRaPzpTf,
div._3nSp9cdBpqL13CqjdMr2L_
{
    width: 1000px !important;
    max-width: 100%;
}

div[data-click-id="media"] > div:first-child {
    display: none;
}
div[data-click-id="media"] > div:last-child {
    position: relative;
}

div[data-click-id="media"],
div[data-click-id="media"] a > div,
div[data-click-id="media"] a > div img
{
    height: auto;
    max-height: 75vh !important;
}
div[data-click-id="media"] iframe.media-element {
    height: /*[[EmbededHeight]]*/px;
}
}
