css:
@-moz-document domain("craftinginterpreters.com") {
.codehilite, code {
    filter: invert(100%) hue-rotate(180deg);
    overflow: visible;
}

.chapter img{
    filter: invert(86.5%) hue-rotate(180deg);
}

body {
    background: #222;
    color: #CCC;
}

main {
	background: #333;
}

footer .next {
	background: #222;
}

footer .next:hover {
	color: #a8dffa;
	background: #222;
}

article.chapter .challenges, article.chapter .design-note {
    color: #CCC;
    background: #333;
}

article.chapter h2 a, article.chapter h3 a {
    color: #CCC;
}

article.chapter h2 a:hover, article.chapter h3 a:hover {
    color: #FFF;
}
}
