css:
@-moz-document domain("freespeech.tv") {
body {
    background: #242932;
}

.main-episode-player-container {
    background: #242932 !important;
    border: none !important;
    border-radius: 4px;
}

p {
    font-family: sans-serif !important;
}


section.home-jumbotron.jumbotron {
    background-image: none !important;
    background: #2E3440;
}

section.home-jumbotron.jumbotron {
    box-shadow: none;
}

.navbar-spark-mod {
    background-color: #2E343F;
}

.d-none.d-md-flex {
    display: none !important;
}

img.dropdown-toggle-image.spark-nav-profile-photo {
    display: none;
}

a.notification-pill.mx-auto.mb-3.mb-md-0.mr-md-0.ml-md-0.text-dark {
    background: #242931;
    color: #4C566A !important;
}

.container-fluid {
    background: #2E343E;
}

img.nav-logo {
    width: 138px;
    padding-top: 14px;
}

footer.page-footer.font-small.blue.pt-4 {
    display: none;
}

main.not-home {
    background: #2E343D;
}

list-group-item {
    position: relative;
    display: block;
    padding: .75rem 1.25rem;
    margin-bottom: -1px;
    background-color: #fff;
    border: 1px solid rgb(36, 41, 50) !important;
}

.container-fluid {
    padding-top: 10px;
}

jumbotron.jumbotron.text-center {
    padding: 0px;
}

h1.jumbotron-heading.mt-5 {
    color: white !important;
    text-shadow: none !important;
    margin-top: 0px !important;
    font-size: 32px;
}

p.lead {
    text-shadow: none !important;
    color: white !important;
    font-size: 16px;
    max-width: 600px;
}

section.home-jumbotron.jumbotron.text-center {
    text-align: left !important;
}

.home-jumbotron {
    background-size: cover;
    background-position: bottom;
    border-radius: 0;
    min-height: 0px;
}

h3 {
    display: none;
}
}
