css:
@-moz-document regexp("http(s)?:\\/\\/www\\.1001tracklists\\.com\\/.*") {
body {
	background-color: #353535;
	color: #eaeaea;
	text-shadow: .3px .3px black;
}
div.main, #main, div.mediaTabItem {
	background-color: #353535;
}
#fixTop {
	background-color: #606060;
	border-bottom: 1.5px solid #757575;
}
.topBorder {
	background-color: #404040;
}
.secondRow {
	background-color: #606060;
}
ul.tab-bar {
	background-color: #696969;
}
.tab-bar-head, .tab-btn-active, table.default th {
	color: #353535;
	background-color: #C5D6E7;
}
select {
	color :#353535;
}
.blueTxt, a {
	color: #50ADFF;
}
.blackTxt {
	color: white;
	text-shadow: none;
}
#wavoWrapperCenter {
	visibility: hidden;
}
}
