css:
@-moz-document url-prefix("http://wiki.joyme.com/arknights/%E5%B9%B2%E5%91%98%E6%95%B0%E6%8D%AE%E8%A1%A8") {
input[type="button"] {
    -webkit-border-radius: 22;
    -moz-border-radius: 22;
    border-radius: 4px;
    font-family: 微软雅黑;
    color: #fff;
    font-size: 16px;
    background: #409eff;
    padding: 4px 9px!important;
    text-decoration: none;
    text-align: center;
    /*     border: none; */
    outline: none;
    margin: 4px 3px;
    white-space: nowrap;
    border: 1px solid #dcdfe6;
    cursor: pointer;
    line-height: 1;
}
input[type="button"]:hover {
    background: #66b1ff;
    border-color: #66b1ff;
    color: #fff;
}
.selected {
    outline: none;
    background: #cf6ad9!important;
    background-color: #cf6ad9!important;
    color: #fff!important;
}
#wrapper {
    background: #FFF;
}
}
