css:
@-moz-document regexp("^.*rokolandia.wordpress.com.*") {
footer, .hfeed site, head, body, .comments-area, .site-content .site-navigation, .site-main, .site-content article, .site-footer, .site-info, body, .widget-area, wrapper, .page-header, .site-header, .site-description {
    background-color: black;
    color: white;
}

#page:before {
    background-color: black;
    color: white;
}

img {
    -webkit-filter: grayscale(65%);
    filter: grayscale(66%);
}
a:link {
    color: white;
}
a:visited {
    color: white;
}
}
