css:
@-moz-document url-prefix("https://forums.xilinx.com") {
.lia-message-VIEW_COUNT {
    display: none;
}
.lia-message-author-registered-date {
    display: none;
}
.lia-component-message-view-widget-tags {
    display: none;
}
.lia-user-avatar-message {
    display: none;
}
.lia-quilt-row-message-subject {
    display: none;
}
.lia-quilt-row-message-controls {
    display: none;
}
.lia-quilt-row-message-body {
    background: #f2f2f2;
    border: solid 1px #fdd;
    border-radius: 10px;
    padding: 10px;
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.3);
}
.lia-quilt-row-message-header {
    float: left;
    margin: 10px;
}
.lia-quilt-row-message-post-times {
    float: right;
    margin: 10px;
}
.lia-message-author-rank {
    font-size: 12px;
}
:not(a) > img {
    border: 1px solid #fdd;
}
.lia-quilt-row-main {
    min-width: 100%;
}
.default-menu-option {
    display: none;
}
button > img {
    border: none;
}
.lia-panel-message {
    margin: 0 !important;
}
}
