css:
@-moz-document domain("animaljam.com") {
    /*swirl tile*/
    .gamepage:before {
        filter: grayscale(2);
    }
    /*gameframe*/
    .gamepage #game-embed-border {
		filter: grayscale(2);
    }
    /*aj logo*/
    .gamepage .aj-logo {
        filter: grayscale(2);
    }
    /*links color*/
    .gamepage .footer ul.footer-links a:link,
    .gamepage .footer ul.footer-links a:visited {
        color: rgba(255, 255, 255, 0.7);
    }
    /*copyright color*/
    .gamepage .footer p.copyright {
        color: rgba(255, 255, 255, 0.7);
    }
    /*ww logo color*/
    .gamepage .footer .footer-content .ww-logo {
        filter: grayscale(2) opacity(.5);
    }
    /*bg color img*/
    .gamepage {
        background-color: #494949;
        background-image: linear-gradient(180deg, #ffffff 0.89%, #494949 100%);
        background-repeat: no-repeat;
        margin-bottom: 0;
    }
}

