css:
@-moz-document domain("samlib.ru") {
/* SAMLIB only article text affected */

/* TEXT & BACKGROUND */
html {
    color: #fffbdf;
    background-color: #353535;
}

/* LINKS */
a {
    color: #dffbff;
    background-color: inherit;
}

/*  ###########
    dependent items 
    ###########  */

body {
    color: inherit;
    background-color: inherit;
}
font {
    color: inherit;
    background-color: inherit;
}

/* TABLE */
table {
    color: inherit;
    background-color: inherit;
}
td {
    color: inherit;
    background-color: inherit;
}

/* Litnet */
.litnet-clr {
    color: inherit;
    background-color: inherit;
}
}
