css:
/* i really want this to be global */

/* Ace editor */

.ace-tm .ace_cursor {
    opacity: 1 !important;
}

/* CodeMirror */

.CodeMirror-cursor {
    visibility: visible !important;
}

/* Monaco */

.monaco-editor .cursors-layer .cursor {
    visibility: visible !important;
}

/* jQuery Terminal */

.cmd .cursor.blink, .cmd .cursor.blink span span {
    animation-duration: 1000000000s !important;
}
