@-moz-document domain("twitter.com") {
div.AdaptiveMedia.is-square {
    max-height: 5060px !important;
}

div.AdaptiveMedia.is-square img {
    top: 0px !important;
}

div.permalink-replies div.AdaptiveMedia-videoContainer div.PlayableMedia--gif  {
    max-width:300px !important;   
    /*max-height:250px !important; */  
}

/*Fix huge memes in replies*/
div.permalink-replies div.AdaptiveMedia.is-square {
    max-width:300px;
}

/*Fix huge videos in replies*/
div.permalink-replies div.AdaptiveMedia-videoContainer div.PlayableMedia--video  {
    max-width:300px !important;   
    /*max-height:250px !important; */  
}


div.permalink-tweet div.AdaptiveMedia-videoContainer div.PlayableMedia--gif  {
    max-width:350px !important;   
   /* max-height:250px !important; */  
}

ol.stream-items div.AdaptiveMedia-videoContainer div.PlayableMedia--gif  {
    max-width:350px !important;   
   /* max-height:250px !important;  */ 
}
}
