css:
@-moz-document domain("www.nitrotype.com")
{
body
{
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
}
  
html
{
    background: fixed url("/*[[bgi]]*/") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
}

.theme--pDefault .profile-bg
{
    background: url("/*[[pbgi]]*/") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
}

.theme--pDefault .profile-bgHead
{
    background: url("/*[[pbgi]]*/") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
}

.theme--pDefault.is-gold .profile-bg
{
    background: url("/*[[pbgi]]*/") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
}

.btn--primary
{
    background: /*[[ac]]*/;
    border:  /*[[ac]]*/;
  	color: /*[[bfc]]*/;
}
  
.btn--primary:hover
{
    background: /*[[ac]]*/;
    border:  /*[[ac]]*/;
  	color: /*[[bfc]]*/;
}
  
.btn--primary:focus
{
    background: /*[[ac]]*/;
    border:  /*[[ac]]*/;
  	color: /*[[bfc]]*/;
}

.btn.btn--outline:not(.is-active):not(.is-disabled):not(:disabled):not(.is-success):not(.is-failure):not(:hover).btn--light, .btn--light
{
	 background-color: /*[[cc]]*/;
     border: 1px solid /*[[cc]]*/;
  	 color: /*[[bfc]]*/;
}

.btn--light:hover, .btn--light:focus
{
	 background-color: /*[[cc]]*/;
     border: 1px solid /*[[cc]]*/;
  	 color: /*[[bfc]]*/;
}
  
.btn--positive
{
     background: /*[[bc]]*/;
     border: 1px solid /*[[bc]]*/;
  	 color: /*[[bfc]]*/;
}
  
.btn--positive:hover 
{
     background: /*[[bc]]*/;
     border: 1px solid /*[[bc]]*/;
  	 color: /*[[bfc]]*/;
}
  
.btn--positive:focus
{
     background: /*[[bc]]*/;
     border: 1px solid /*[[bc]]*/;
  	 color: /*[[bfc]]*/;
}
  
.btn.btn--outline:not(.is-active):not(.is-disabled):not(:disabled):not(.is-success):not(.is-failure):not(:hover), .btn--negative, .btn.btn--outline:not(.is-active):not(.is-disabled):not(:disabled):not(.is-success):not(.is-failure):hover, .btn.btn--outline:not(.is-active):not(.is-disabled):not(:disabled):not(.is-success):not(.is-failure):hover:focus
{
	 background-color: /*[[ic]]*/;
  	 border: 1px /*[[ic]]*/;
  	 color: /*[[bfc]]*/;
}
  
.btn--negative:hover, .btn--negative:focus
{
	 background-color: /*[[ic]]*/;
  	 border: 1px /*[[ic]]*/;
  	 color: /*[[bfc]]*/;
}

.notify:before
{
	 background: /*[[nc]]*/;
}
  
.notify
{
	 background: /*[[nc]]*/;
  	 color: /*[[bfc]]*/;
}

 .structure-ad
  {
    display: none;
  }

  .ad
  {
    display: none;
    position: relative;
    margin-right: auto;
    margin-left: auto;
    text-align: center;
  }

  .ad--sq
  {
    display: none;
    width: 336px;
    height: 280px;
    background: #f2f2f2;
  }

  .ad--ldrbrd
  {
    display: none;
    width: 728px;
    height: 90px;
    background: #f2f2f2;
  }

  .ad--ldrbrd_l
  {
    display: none;
    width: 970px;
    height: 90px;
  }

  .ad--fixed
  {
    display: none;
    position: fixed;
    top: 30px;
  }

  .structure-header
  {
    margin-left: auto;
    margin-right: auto;
    max-width: 1020px;
    padding-left: 0px;
    padding-right: 0px;
  }
  
  .news-teaser
{
    color: /*[[fc]]*/;
}

.news--solo .news-body p
{
    color: /*[[fc]]*/;
}

.news-header
{
    color: /*[[fc]]*/;
}

.card--b
{
    color: /*[[fc]]*/;
}

.news-byline
{
    color: /*[[fc]]*/;
}

.comment-body
{
    color: /*[[fc]]*/;
}

.tc-i
{
    color: /*[[fc]]*/;
}

.tc-ts
{
    color: /*[[fc]]*/;
}

html
{
    color: /*[[fc]]*/;
}

.card--d
{
    color: /*[[fc]]*/;
}

h3
{
    color: /*[[fc]]*/;
}

.achievement
{
    color: /*[[fc]]*/;
}

.input-label
{
    color: /*[[fc]]*/;
}

.card--e
{
    color: /*[[fc]]*/;
}

.card--a
{
    background-color: #fff0;
}

.card--shadow-l
{
    box-shadow: none;
}

.news-ads
{
    display: none;
}

.card--grit
{
    background-image: url("/*[[sbi]]*/");
}
  
.dash-copyContainer
{
    background: /*[[tbc]]*/;
}
  
.dash-copy
{
    color: /*[[fc]]*/;
}

.dash-letter.is-correct
{
    color: /*[[fc]]*/;
}

::selection
{
	background: /*[[hlc]]*/;
  	color: /*[[fc]]*/;
}
  
.dash-copy
{
    font-family: "/*[[rf]]*/", monospace;
}

.header {
    background: url("/*[[bbg]]*/") no-repeat center 0 #0000;
}

.logo-SVG {
    display: none;
}
  
 .dash-copy
{
    font-size: /*[[rmfs]]*/px;
}

.dash-copyContainer.is-single-line .dash-copy .dash-letter
{
    font-size: /*[[rsfs]]*/px;
}
}
