css:
@-moz-document domain("nuget.org") {
body {
	background: #111;
	color: #ccc;
}
#search,
.package-manager-tab[aria-expanded="false"] {
	background: #222 !important;
	color: #ccc !important;
}
.package-title {
	text-decoration: underline;
}
.ms-Icon,
.package-legacy-title {
	color: #555;
	text-decoration: none;
}
h2 span,
.package-details {
	color: #888!important;
}
.tag,
.bg-info {
	background: #002440;
}
}
