css:
@-moz-document url-prefix("https://portal.kftg.ch/") {
body {
    background-color: rgb(64, 64, 64);
}

div #suiteBarLeft {
    background-color:#323232;
    color:#fff;
}

.ms-core-brandingText {
    visibility: hidden;
}

.ms-core-brandingText:after {
    visibility: visible;
    margin-left: -70px;
    content:'portal.kftg.ch';
}

#DeltaSiteLogo a {
    visibility: hidden;
}

#siteIcon {
    display: none;
}

.ms-core-overlay {
	background-color: rgba( 255,255,255,0 );
}

#s4-titlerow {
 	display: block;
    padding-bottom: 0px;
    height: 50px;
    padding-top: 10px;
    background-color: #252525;
}

#globalNavBox, .ms-clientcontrol-chromeheader {
    background-color: rgba( 30,30,30,0.78 );
    width: 100%;
    height: 35px;
}

#MSOZoneCell_WebPartWPQ6, #WebPartWPQ5, #WebPartTitleWPQ5 {
	display: none;
}

#zz10_RootAspMenu .static {
    visibility: hidden;
    width: 0px;
}

#zz10_RootAspMenu .dynamic-children {
    visibility: visible;
    width: auto;
    font-size: 25px;
    margin-left: 20px;
}

#ctl00_PlaceHolderSearchArea_SmallSearchInputBox1_csr_sboxdiv {
 	display: none;   
}

.ms-vb a.ms-listlink, .ms-vb2 a.ms-listlink, .ms-vb-user a.ms-listlink, .ms-vb a.ms-listlink:visited, .ms-vb a.ms-listlink:visited:hover {
    color: white;
    text-decoration: none;
}

.ms-vb, .ms-vb2, .ms-vb-user, .ms-vb-tall, .ms-pb, .ms-pb-selected {
    color: white;
}

.ms-subtleLink:link, .ms-subtleLink:visited, .ms-atMention:link, .ms-atMention:visited, .ms-hashTag:link, .ms-hashTag:visited {
    color: white;
}

.ms-core-listMenu-item, .ms-core-listMenu-item:link, .ms-core-listMenu-item:visited, .ms-tv-item:link, .ms-tv-item:visited, .ms-tv-header:link, .ms-tv-header:visited {
    color: #ccc;
}

#zz12_RootAspMenu .selected a span {
 	color: black;   
}

.ms-webpart-titleText a nobr span {
 	color: white;   
}

.dynamic-children ul {
 	background-color: rgb(64, 64, 64);   
}

.dynamic-children ul li a{
 	font-size: 20px;  
}

.contentBox {
    display: block;
}

#DeltaPlaceHolderMain {
	margin-top: 40px;
	margin-left: 10px;
}

#pageTitle {
	margin-top: 10px;
	margin-left: 30px;
}

.ms-core-menu-box {
	background-color: #353535;
}

.ms-core-menu-title {
	color: white;
}

#fmi_cls {
	background-color: #303030;
	color: white;
}

#DeltaPlaceHolderPageTitleInTitleArea {
	margin-left: 200px;
}

#DeltaPlaceHolderPageTitleInTitleArea a{
	color: white;
}
}

@-moz-document url-prefix("https://portal.kftg.ch/personal/") {
.ms-core-pageTitle {
    margin-top: 20px;
    margin-bottom: 10px;
    margin-left: 10px;
}

.ms-status-blue {
    margin-top: 10px;
}

#sideNavBox.ms-mysite-sideNavBox {
    background-color: #303030;
}

#zz12_RootAspMenu .selected a span {
    color: white;
}

#pageTitle {
	margin-top: 30px;
}

#pageTitle a{
	color: white;
}

.ms-status-blue {
	display: none !important;
}
}

@-moz-document url("https://portal.kftg.ch/default.aspx") {
#ctl00_PlaceHolderLeftNavBar_PlaceHolderQuickLaunchBottom_idNavLinkViewAll {
    visibility: hidden;
}

#MSOZoneCell_WebPartWPQ2 {
    visibility: hidden;
}

body, .ms-core-defaultFont, .js-callout-body {
    color: #fff;
}
}
