/*#####--- regexp 匹配正則表示式的網址 ---#####*/
@-moz-document url-prefix("https://patents.google.com/"), regexp("((?!google.com|youtube.com|facebook.com).)*") {
/* 網頁醇黑風-1 */


	/*#####--- 網頁文字、背景顏色 ---#####*/
/*#####--- 文字顏色 #fffff0-象牙白 #fffacd-檸檬綢色 #A6FFCC-蒼色 #73E68C-青瓷綠 #4DE680-綠松石綠  #00FA9A-中春綠色 ---#####*/
* { background-color: #000000 !important; 
    color: #A6FFCC !important;  /* #A6FFCC-蒼色 */
    font-family:  "微軟正黑體" !important;
    letter-spacing: 0.1em !important;
    line-height: 1.4em !important; }

h1, h2, h3 { vertical-align: baseline; color: #FFE4E1 !important; } /* 標題 - 霧玫瑰色 */

Strong { color: #cc99ff !important; }  /* 粗體字強調語句 #cc99ff-淺石英紫   #ca9cfe-紫芋  #CDB4E7-淡紫 */

	/*#####--- 網址超連結顏色 ---#####*/
a:link { color: #00FFFF !important; }
a:hover { color: #FFFF00 !important; }
a:visited { color: #CCCCFF !important; }

	/*#####--- 搜尋欄輸入關鍵字、下拉選單、文字欄位 的醒目顏色(亮黃色) ---#####*/
INPUT,Option,Textarea { color: #8b008b !important; padding-left: 5px !important; background: none !important; background-color: #fffacd !important; border: 2px solid #FF00FF !important; }

	/*#####--- 滑鼠選取區 ---#####*/
::Selection {
    color: #FFFF99 !important;  /* 香檳黃 */
    background: #2a0155 !important;}

	/*#####--- Scroll Bar滾動條外觀 ---#####*/
::-webkit-scrollbar { width: 20px; }
::-webkit-scrollbar-track { box-shadow: inset 0 0 5px grey; border-radius: 10px; }
::-webkit-scrollbar-thumb { background: #5F388F; border-radius: 10px; }
::-webkit-scrollbar-thumb:hover { background: #351061; }





	/*#####--- AutoPagerize 網頁自動換頁免點下一頁 (第X頁) ---#####*/
a[class="autopagerize_link"] { color:   #FFFF00    !important;font-size: 21px !important; }

	/*#####--- 隱藏 痞客邦 右側邊欄 下方留言區跳出登入方框 ---#####*/
DIV[id="pilot"], DIV[class="pixgame"] { display: none !important; }
	/*#####--- 隱藏 隨意窩 右側邊欄 ---#####*/
DIV[class="selectbar"] { display: none !important; }
	/*#####--- 隱藏 網路攻略 net·work·er 右側邊欄 ---#####*/
DIV[id*="essb_displayed_sidebar_"][class*="essb_links essb_counters essb_displayed_sidebar essb_"] { display: none !important; }
	/*#####--- 隱藏 Mobile 右側方框 ---#####*/
DIV[class="ad_left_bottom"] { display: none !important; }
	/*#####--- Google Patents 專利文件 ---#####*/
DIV[id="wrapper"][class="style-scope patent-result"] { font-size: 20px !important; letter-spacing: 0.1em !important; line-height: 1.5em !important; }


}



@-moz-document url-prefix("https://mouniassn.pixnet.net/") {
/* 牟尼精舍 :: 痞客邦 :: */

	/*#####---  縮減文章方框左右的白色邊框 ---#####*/
DIV[class="article"] { margin-right: -20px !important;  margin-left: -20px !important; } 
UL[class="article-head"] { margin-right: 20px !important;   margin-left: 20px !important; }

	/*#####---  下方留言區文字 ---#####*/
LI[class="post-text"] { color: #e7d2fb !important; font-size: 20px !important; letter-spacing: 0.1em !important; line-height: 1.5em !important; }	

	/*#####---  下方留言區回復文字 - 霧玫瑰色 ---#####*/	
LI[class="reply-text"] { color: #FFE4E1 !important; font-size: 20px !important; letter-spacing: 0.1em !important; line-height: 1.5em !important; }	

	/*#####---  下方送出留言區 ---#####*/
TEXTAREA[id="comment_text"][name="blogcommenttext_body"] { color: #8b008b !important; font-size: 19px !important; padding-left: 5px !important; background: none !important; background-color: #fffacd !important; border: 2px solid #FF00FF !important; height: 500px !important; }

	/*#####---  清除留言區及最上方登錄雜物 ---#####*/
DIV[class="choose-identity"], DIV[class="tag-container-parent"], DIV[class*="pixnavbar__top-bar"] {display: none !important;}

	/*#####---  調整最上方圖片方框距離  ---#####*/
DIV[id="banner"] { font-size: 9px !important;  margin-top: -70px !important; }
DIV[id="header"] { padding-top: 75px !important; }
UL[id="navigation"]  { margin-top: 95px !important; }


}
